(()=>{gsap.registerPlugin(ScrollTrigger);function i(){document.querySelectorAll(".home_features_item").forEach((e,o)=>{let t=gsap.timeline({paused:!0}),r=window.innerWidth>=768&&o%2===1?.5:0;switch(t.fromTo(e.querySelectorAll(".home_features_item_content > *"),{opacity:0,y:30},{opacity:1,y:0,duration:.6,stagger:{each:.05},delay:r}),o){case 0:t.to(e.querySelector(".home_features_toolkit_img"),{y:0,opacity:1,duration:.6},"<20%");break;case 1:t.to(e.querySelector(".home_features_dash_img"),{y:0,opacity:1,duration:.6},"<20%");break;case 2:t.to(e.querySelector(".private_graphic_phone"),{y:0,opacity:1,duration:.6},"<20%").to(e.querySelector(".private_graphic_chat_1"),{y:0,opacity:1,duration:.6},"<80%").to(e.querySelector(".private_graphic_chat_2"),{y:0,opacity:1,duration:.6},"<80%");break;case 3:t.to(e.querySelector(".home_features_ramp_img"),{y:0,opacity:1,duration:.6},"<20%").to(e.querySelector(".home_features_ramp_img"),{opacity:0,duration:.6},1.8).to(e.querySelector(".home_features_ramp_img_abs"),{opacity:1,duration:.6},1.8).to(e.querySelector(".home_features_ramp_img"),{opacity:1,duration:.6},3).to(e.querySelector(".home_features_ramp_img_abs"),{opacity:0,duration:.6},3);break}ScrollTrigger.create({trigger:e,start:"top 70%",end:"bottom 30%",onEnter:()=>t.play()})})}})();
