(()=>{gsap.registerPlugin(SplitText,ScrollTrigger);document.fonts.ready.then(()=>{if(!document.querySelector("[data-hero-animation]"))return;let e=new SplitText("[data-hero-animation] h1",{type:"lines",linesClass:"hero_text_line"}),t=new SplitText("[data-hero-animation] p",{type:"lines",linesClass:"hero_text_line"});gsap.set("[data-hero-animation] :is(h1, p)",{opacity:1}),gsap.timeline().to("[data-hero-animation] .eyebrow_text",{opacity:1,y:0,duration:.5,ease:"power2.out"}).to(e.lines,{opacity:1,y:0,duration:.5,stagger:{each:.05}},"<25%").to(t.lines,{opacity:1,y:0,duration:.5,stagger:{each:.05}},"<35%").to("[data-hero-animation] .button_main_wrap",{opacity:1,y:0,duration:.5,ease:"power2.out",stagger:{each:.025}},"<25%")});})();
